+        unsigned char buf[32];
+        secp256k1_sha256 sha_tmp = sha;
+        secp256k1_sha256_write(&sha_tmp, &i, 1);
+        secp256k1_sha256_finalize(&sha_tmp, buf);
+        secp256k1_scalar_set_b32(&k[i], buf, NULL);
+    }
+}
