+    if (seckey != NULL) {
+        secp256k1_scalar sk;
+        ret &= secp256k1_scalar_set_b32_seckey(&sk, seckey);
+        secp256k1_scalar_clear(&sk);
+    }
+
+    if (keyagg_cache != NULL) {
