+    /* A pubkey cannot be the point at infinity */
+    VERIFY_CHECK(pk_serialize_success);
+    VERIFY_CHECK(pk_ser_len == sizeof(pk_ser));
+#else
+    (void) pk_serialize_success;
+#endif
