+int memcmp_and_randomize(unsigned char *value, const unsigned char *expected, size_t len) {
+    int ret;
+    size_t i;
+    ret = secp256k1_memcmp_var(value, expected, len);
+    for (i = 0; i < len; i++) {
+        value[i] = testrand_bits(8);
+    }
+    return ret;
+}
