+    testrand_flip(args[n_flip], n_bytes);
+    secp256k1_nonce_function_musig(k2, args[0], args[1], args[2], args[3], args[4], args[5]);
+    CHECK(secp256k1_scalar_eq(&k1[0], &k2[0]) == 0);
+    CHECK(secp256k1_scalar_eq(&k1[1], &k2[1]) == 0);
