+    memcpy(ptr, secp256k1_musig_keyagg_cache_magic, 4);
+    ptr += 4;
+    secp256k1_ge_to_bytes(ptr, &cache_i->pk);
+    ptr += 64;
+    secp256k1_ge_to_bytes_ext(ptr, &cache_i->second_pk);
+    ptr += 64;
+    memcpy(ptr, cache_i->pks_hash, 32);
+    ptr += 32;
