+        ret = secp256k1_eckey_pubkey_serialize(&ges[i], &out66[33*i], &size, 1);
+#ifdef VERIFY
+        /* serialize must succeed because the point was just loaded */
+        VERIFY_CHECK(ret && size == 33);
+#else
+        (void) ret;
+#endif
