+    int ret;
+    secp256k1_keypair keypair_tmp;
+    ret = secp256k1_keypair_create(CTX, &keypair_tmp, sk);
+    ret &= secp256k1_keypair_pub(CTX, pk, &keypair_tmp);
+    if (keypair != NULL) {
+        *keypair = keypair_tmp;
+    }
+    return ret;
