+    secp256k1_fe_get_b32(fin_nonce, &fin_nonce_pt.x);
+    secp256k1_fe_normalize_var(&fin_nonce_pt.y);
+    *fin_nonce_parity = secp256k1_fe_is_odd(&fin_nonce_pt.y);
+    return 1;
+}
