+        secp256k1_sha256_write(&sha, session_secrand, 32);
+        secp256k1_sha256_finalize(&sha, rand);
+        for (i = 0; i < 32; i++) {
+            rand[i] ^= seckey32[i];
+        }
+    } else {
+        memcpy(rand, session_secrand, sizeof(rand));
+    }
+
+    /* Subtract one from `sizeof` to avoid hashing the implicit null byte */
