+    secp256k1_sha256_write(&sha, &msg_present, 1);
+    if (msg_present) {
+        secp256k1_nonce_function_musig_helper(&sha, 8, msg32, 32);
+    }
+    secp256k1_nonce_function_musig_helper(&sha, 4, extra_input32, 32);
