+    secp256k1_ecmult_const(&ss_j, &pk, secret_component);
+    secp256k1_ge_set_gej(&ss, &ss_j);
+    ret &= secp256k1_eckey_pubkey_serialize(&ss, shared_secret33, &len, 1);
+    return ret;
