+    secp256k1_ge result_ge;
+    int ret;
+
+    /* Sanity check inputs. */
+    VERIFY_CHECK(ctx != NULL);
+    ARG_CHECK(labelled_spend_pubkey != NULL);
+    ARG_CHECK(recipient_spend_pubkey != NULL);
+    ARG_CHECK(label != NULL);
