+static int secp256k1_silentpayments_recipient_public_data_load_pubkey(const secp256k1_context *ctx, secp256k1_pubkey *pubkey, const secp256k1_silentpayments_recipient_public_data *public_data) {
+    size_t pubkeylen = 65;
+    return secp256k1_ec_pubkey_parse(ctx, pubkey, &public_data->data[1], pubkeylen);
+}
+
+static void secp256k1_silentpayments_recipient_public_data_load_input_hash(unsigned char *input_hash, const secp256k1_silentpayments_recipient_public_data *public_data) {
+    size_t pubkeylen = 65;
+    memcpy(input_hash, &public_data->data[1 + pubkeylen], 32);
+}
