+    if (!secp256k1_eckey_pubkey_tweak_add(&P_output_ge, &t_k_scalar)) {
+        secp256k1_scalar_clear(&t_k_scalar);
+        return 0;
+    };
+    secp256k1_xonly_pubkey_save(P_output_xonly, &P_output_ge);
