+static int parse_iterations(const char* arg) {
+    if (!arg) return 0;
+    COUNT = (int) strtol(arg, NULL, 0);
+    if (COUNT <= 0) {
+        fputs("An iteration count of 0 or less is not allowed.\n", stderr);
+        return -1;
