+#if SUPPORTS_CONCURRENCY
+/* Process tests in parallel */
+static int run_concurrent(struct TestFramework* tf) {
+    /* Sub-processes info */
+    pid_t workers[MAX_SUBPROCESSES];
+    int pipes[MAX_SUBPROCESSES][2];
+    /* Next worker to send work */
