+        pid_t pid;
+        if (pipe(pipes[it]) != 0) {
+            perror("Error during pipe setup");
+            return EXIT_FAILURE;
+        }
+
+        pid = fork();
+        if (pid < 0) {
+            perror("Error during process fork");
+            return EXIT_FAILURE;
+        }
+
+        if (pid == 0) {
+            /* Child worker: run tests assigned via pipe */
+            TestRef tref;
+            close(pipes[it][1]); /* Close write end */
+            while (read(pipes[it][0], &tref, sizeof(tref)) == sizeof(tref)) {
