+/* Read args: all must be in the form -key=value, --key=value or -key=value */
+static int read_args(int argc, char** argv, int start, struct TestFramework* tf) {
+    int i;
+    const char* key;
+    const char* value;
+    int is_long;
+    char* eq;
+    for (i = start; i < argc; i++) {
+        char* raw_arg = argv[i];
+        if (!raw_arg || raw_arg[0] != '-') {
+            fprintf(stderr, "Invalid arg '%s': must start with '-'\n", raw_arg ? raw_arg : "(null)");
+            return -1;
+        }
+
+        eq = strchr(raw_arg, '=');
+        if (!eq || eq == raw_arg + 1) { /* key is empty */
+            fprintf(stderr, "Invalid arg '%s': must be -k=value or --key=value\n", raw_arg);
+            return -1;
+        }
+
+        value = eq + 1;
+        if (!value || *value == '\0') { /* value is empty */
+            fprintf(stderr, "Invalid arg '%s': value cannot be empty\n", raw_arg);
+            return -1;
+        }
+
+        *eq = '\0'; /* split key and value */
+        key = normalize_key(raw_arg, &is_long);
+        if (!key || *key == '\0') {
+            fprintf(stderr, "Invalid key: '%s'\n", raw_arg);
+            return -1;
+        }
+
+        /* forbid --x (double-dash with single-char key) */
+        if (is_long && key[1] == '\0') {
+            fprintf(stderr, "Invalid key: short option '%s' cannot use double dash\n", raw_arg);
+            return -1;
+        }
+
+        if (parse_arg(key, value, tf) != 0) return -1;
+    }
+    return 0;
+}
