+    secp256k1_point_multiply(&res2, &secp256k1_ge_const_g, &b);
+    secp256k1_ge_set_gej(&mid1, &res1);
+    secp256k1_ge_set_gej(&mid2, &res2);
+    secp256k1_point_multiply(&res1, &mid1, &b);
+    secp256k1_point_multiply(&res2, &mid2, &a);
+    secp256k1_ge_set_gej(&mid1, &res1);
