+    ge_equals_ge(&mid1, &mid2);
+}
+
+void ecdh_mult_zero_one(void) {
+    secp256k1_scalar_t zero = SECP256K1_SCALAR_CONST(0, 0, 0, 0, 0, 0, 0, 0);
