+        secp256k1_gej_neg(&pubnon, &pubnon);
+    }
+    secp256k1_gej_add_var(&aggctx->pubnonce_sum, &aggctx->pubnonce_sum, &pubnon, NULL);
+    aggctx->progress[index] = NONCE_PROGRESS_OURS;
+    return 1;
