+    CHECK(secp256k1_eckey_pubkey_serialize(nonce_ge, buf, &buflen, 1));
+    secp256k1_sha256_write(&hasher, buf, sizeof(buf));
+    /* Encode message */
+    secp256k1_sha256_write(&hasher, msghash32, 32);
+    /* Finish */
+    secp256k1_sha256_finalize(&hasher, output);
