+        secp256k1_scalar_set_b32(sc, buf, NULL);
+        secp256k1_scalar_negate(sc, sc);
+        secp256k1_scalar_mul(sc, sc, &ecmult_context->randomizer_cache[(idx / 2) % 2]);
