+    r3  = 0x3FFFFFFUL & t;
+    r4  = 0x3FFFFFFUL & t;
+    r5  = 0x3FFFFFFUL & t;
+    r6  = 0x3FFFFFFUL & t;
+    r7  = 0x3FFFFFFUL & t;
+    r8  = 0x3FFFFFFUL & t;
